import React from 'react'
import styled from 'styled-components';

function ConditionalRenderList({ value, list, setSearch, toggle, setToggle }) {
    if (value) {
        const filteredList = list.filter(item => item.title.toString().toLowerCase().startsWith(value.toLowerCase()));

        if (filteredList.length) {
            return (
              toggle &&
              (
                  <StyledList>
                      {filteredList.map((item, i) => <StyledItem key={i} onClick={() => { setToggle(false); setSearch(item.title) }}>{item.title}</StyledItem>)}
                  </StyledList>
              )
          )
        }
        return (
            <StyledList>
                <StyledItem>Not Found</StyledItem>
            </StyledList>
        )
    }
    return null;
}

const StyledList = styled.div`

`

