import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { NavLink as Link } from 'react-router-dom';
import logo from '../images/ufo.png';
import SearchableDropdown from './dropdown/SearchableDropdown';

function Navbar() {
    const API_URL = "http://localhost:3000";
    const [titleList, setTitleList] = useState([]);

    useEffect(() => {
        fetch(`${API_URL}/posts`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        })
            .then((res) => res.json())
            .then((data) => {
                setTitleList(data)
            });
    }, []);
    return (
        <>
            <Nav>
                <div className='container'>
                    <div className='logo'>
                        <img src={logo} alt='' />
                    </div>
                    <h2>GAME REVIEWS</h2>
                    <SearchableDropdown
                        list={titleList}
                        handleOnChange={(e) => console.log(e)}
                    />
                </div>

                <NavMenu>
                    <NavLink to='/myreviews'>My reviews</NavLink>
                </NavMenu>
                <NavBtn>
                    <NavBtnLink to="/signin">Sign In</NavBtnLink>
                </NavBtn>
                {/* <Hamburger>
                    <span></span>
                    <span></span>
                    <span></span>
                </Hamburger> */}
            </Nav>
        </>
    )
}

const Nav = styled.nav`
    background: #F0F4FF;
    border-radius: 15px;
    box-shadow: 10px 10px 20px #ced8f5, -10px -10px 20px #f6f9ff;
    margin: 30px 0;
    height: 60px;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    /* padding: 0.5rem calc((100vw - 1000px) / 2); */
    z-index: 10;

    .container {
        display: flex;
        align-items: center;

        .logo {
            width: 40px;
            height: 40px;
            overflow: hidden;
            margin-left: 15px;

            img {
                max-width: 100%;
                max-height: 100%;
            }
        }

        h2 {
            color: #8A8A8A;
            padding: 5px 10px;
        }
    }
`

const NavLink = styled(Link)`
    font-weight: 600;
    color: white;
    display: flex;
    align-items: center;
    text-decoration: none;
    padding: 0 1rem;
    height: 100%;
    cursor: pointer;

    &:hover {
        color: white;
        background: #1785F8;
    }
`

const NavMenu = styled.div`
    display: flex;
    align-items: center;
    /* margin-right: 15px; */

    /* //768px
    @media screen and (max-width: 890px) {
        display: none;
    } */
`

const NavBtn = styled.button`
    display: flex;
    align-items: center;
    margin-right: 15px;
    border-radius: 10px;
    border: none;

    /* @media screen and (max-width: 890px) {
        display: none;
    } */
`

const NavBtnLink = styled(Link)`
    font-weight: 600;
    border-radius: 10px;
    background: #1C96F9;
    padding: 0 1rem;
    color: white;
    border: none;
    outline: none;
    cursor: pointer;
    transition: all 0.2s ease-in-out;
    text-decoration: none;

    &:hover {
        transition: all 0.2s ease-in-out;
        background: #1785F8;
        color: white;
    }
`

