import React from 'react';
import styled from 'styled-components';
import { FaUser, FaLock, FaAt } from 'react-icons/fa';
import bg from './images/16.jpg';

function Signup() {
  return (
    <ContainerStyled>
      <div className='login-box'>
        <div className='left-content'>
          <img src={bg} alt='bg' />
        </div>
        <form className='right-content'>
          <h2>SIGN UP</h2>
          <div className='field'>
            <div className='icon'>
              <FaAt />
            </div>
            <input
              type='text'
              placeholder='Email'
            />
          </div>
          <div className='field'>
            <div className='icon'>
              <FaUser />
            </div>
            <input
              type='text'
              placeholder='Username'
            />
          </div>
          <div className='field'>
            <div className='icon'>
              <FaLock />
            </div>
            <input
              type='password'
              placeholder='Password'
              autoComplete='on'
            />
          </div>
          <NavBtnLink to='/signup'>Sign Up</NavBtnLink>
        </form>
      </div>
    </ContainerStyled>
  )
}

