import React from 'react';
import styled from 'styled-components';
import { FaUser, FaLock, FaAt } from 'react-icons/fa';
import { NavLink as Link } from 'react-router-dom';
import bg from './images/16.jpg';

function Signup() {
    return (
        <ContainerStyled>
            <div className='login-box'>
                <div className='left-content'>
                    <img src={bg} alt='bg' />
                </div>
                <form className='right-content'>
                    <h2>SIGN UP</h2>
                    <div className='field'>
                        <div className='icon'>
                            <FaAt />
                        </div>
                        <input
                            type='text'
                            placeholder='Email'
                        />
                    </div>
                    <div className='field'>
                        <div className='icon'>
                            <FaUser />
                        </div>
                        <input
                            type='text'
                            placeholder='Username'
                        />
                    </div>
                    <div className='field'>
                        <div className='icon'>
                            <FaLock />
                        </div>
                        <input
                            type='password'
                            placeholder='Password'
                            autoComplete='on'
                        />
                    </div>
                    <NavBtn>
                        <NavBtnLink to='/'>Sign Up</NavBtnLink>
                    </NavBtn>
                </form>
            </div>
        </ContainerStyled>
    )
}

const NavBtn = styled.button`
    display: flex;
    align-items: center;
    border-radius: 20px;
    border: none;
    margin-left: 9rem;
`

const NavBtnLink = styled(Link)`
    font-weight: 700;
    border-radius: 20px;
    background: #BCEA73;
    padding: 10px 22px;
    color: white;
    border: none;
    outline: none;
    cursor: pointer;
    transition: all 0.2s ease-in-out;
    text-decoration: none;

    &:hover {
        transition: all 0.2s ease-in-out;
        background: #B1DC6D;
    }
`

const ContainerStyled = styled.div`
    position: relative;
    display: flex;
