            overflow: hidden;

            img{
                width: 100%;
                height: 100%;
                object-fit: cover;
            }
        }

        .right-content{
            padding: 25px 40px;

            h2{
                position: relative;
                padding-bottom: 10px;
                margin-bottom: 10px;
            }

            h2:after{
                content: '';
                position: absolute;
                left: 50%;
                bottom: 0;
                transform: translateX(-50%);
                height: 4px;
                width: 50px;
                border-radius: 2px;
                background-color: #BCEA73;
            }

            .field{
                min-width: 380px;
                width: 100%;
                height: 55px;
                background-color: #f0f0f0;
                margin: 10px 0;
                border-radius: 30px;
                display: grid;
                grid-template-columns: 15% 85%;
                padding: 0.5rem 1rem;

                .icon{
                    text-align: center;
                    line-height: 45px;
                    color: #acacac;
                    font-size: 1.1rem;
                }

                input{
                    background: none;
                    outline: none;
                    border: none;
                    line-height: 1.6;
                    font-size: 1.1rem;
                    color: black;
                }
            }

            /* .btn{
                width: 150px;
                height: 40px;
                padding: 0.5rem 1rem;
                font-size: 1.1rem;
                background-color: #BCEA73;
                cursor: pointer;
                outline: none;
                border: none;
                border-radius: 30px;
                color: white;
                transition: .3s;
            }

            .btn:hover{
                background-color: #B1DC6D;
            }

            .signup{
              margin-top: 1rem;

              p{
                font-size: small;
                display: inline;
              }
            } */
        }
    }
`

