import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

function CommentSection({ path }) {
  const API_URL = "http://localhost:3000";
  const [text, setText] = useState('');
  const [review, setReview] = useState({});

  useEffect(() => {
    fetch(`${API_URL}/comments/${path}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      }
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data)
        setReview(data)
      });
  }, [path])

  const postData = (e) => {
    e.preventDefault()
    fetch(`${API_URL}/comments/createcomment`, {
      method: 'POST',
      mode: "cors",
      cache: "no-cache",
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + localStorage.getItem('jwt')
      },
      redirect: "follow",
      referrerPolicy: "no-referrer",
      body: JSON.stringify({
        gameId: path,
        text,
      })
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data)
        if (data.error) {
          alert(data.error)
        } else {
          // created comment successfully
        }
      })
      .catch(err => {
        console.log(err)
      })
  }

  return (
    <ContainerStyled>
      <div className='left-content'>
        <p>{review.text}</p>
      </div>
      <div className='right-content'>
        <div className='textfield'>
          <textarea
            placeholder='Write review...'
            value={text}
            onChange={(e) => { setText(e.target.value) }}
          />
        </div>
        <button className='btn' onClick={(e) => postData(e)}>POST</button>
      </div>
    </ContainerStyled>
  )
}

const ContainerStyled = styled.div`
  display: grid;
  /* grid-template-columns: repeat(2, 1fr); */
  grid-template-columns: 2fr 40%;
  justify-content: center;
  align-items: center;
  padding: 1rem;

  .left-content{
    background: palevioletred;
  }

  .right-content{
    padding: .5rem;
    box-shadow: 10px 10px 20px #ced8f5, -10px -10px 20px #f6f9ff;

    .textfield{
      height: 200px;
      width: 100%;
      border-radius: 15px;
      margin: 1rem 0;

      textarea{
        height: 200px;
        width: 100%;
        outline: none;
        border: none;
        border-radius: 10px;
        line-height: 1.4;
        padding: .5rem;
        background: #F0F4FF;
        box-shadow: inset 3px 3px 10px 2px #ced8f5;
        font-family: 'Open Sans', sans-serif;
      }
    }

    .btn{
      width: 80px;
      padding: .25rem .25rem;
      background-color: #1C96F9;
      cursor: pointer;
      outline: none;
      border: none;
      border-radius: 20px;
      color: white;
      transition: .3s;
    }

    .btn:hover{
        background-color: #1785F8;
    }
  }
`

export default CommentSection
