import React, { useState } from 'react';
import styled from 'styled-components';
import { FaUser, FaLock, FaAt } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import bg from './images/16.jpg';

function Signup() {
    const API_URL = "http://localhost:3000";
    const [email, setEmail] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const postData = () => {
        fetch(`${API_URL}/auth/signup`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                email,
                username,
                password
            })
        })
            .then((res) => res.json())
            .then((data) => {
                if (data.error) {
                    alert(data.error)
                }else {
                    navigate('/login', { replace: true })
                }
            });
    }

    return (
        <ContainerStyled>
            <div className='login-box'>
                <div className='left-content'>
                    <img src={bg} alt='bg' />
                </div>
                <form className='right-content'>
                    <h2>SIGN UP</h2>
                    <div className='field'>
                        <div className='icon'>
                            <FaAt />
                        </div>
                        <input
                            type='text'
                            placeholder='Email'
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />
                    </div>
                    <div className='field'>
                        <div className='icon'>
                            <FaUser />
                        </div>
                        <input
                            type='text'
                            placeholder='Username'
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                    </div>
                    <div className='field'>
                        <div className='icon'>
                            <FaLock />
                        </div>
                        <input
                            type='text'
                            placeholder='Password'
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>
                    <Btn>
                        <NavBtn onClick={() => postData()}>Sign Up</NavBtn>
                    </Btn>
                </form>
            </div>
        </ContainerStyled>
    )
}

const NavBtn = styled.button`
    font-weight: 700;
    border-radius: 20px;
    background: #BCEA73;
    padding: 10px 22px;
    color: white;
    border: none;
    outline: none;
    cursor: pointer;
    transition: all 0.2s ease-in-out;
    text-decoration: none;

    &:hover {
        transition: all 0.2s ease-in-out;
        background: #B1DC6D;
    }
`

const Btn = styled.div`
    display: flex;
    align-items: center;
    border-radius: 20px;
    border: none;
    margin-left: 9rem;
`

const ContainerStyled = styled.div`
    position: relative;
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 70vh;
    padding: 20px 100px;

    .login-box{
        max-width: 850px;
        display: grid;
        grid-template-columns: repeat(2, 1fr);
        justify-content: center;
        align-items: center;
        text-align: center;
        background-color: white;
        border-radius: 20px;
        box-shadow: 0px 0px 20px 5px #CED8F5;

        .left-content{
            height: 100%;
            border-radius: 20px 0px 0px 20px;
            overflow: hidden;

            img{
                width: 100%;
                height: 100%;
                object-fit: cover;
            }
        }

        .right-content{
            padding: 25px 40px;

            h2{
                position: relative;
                padding-bottom: 10px;
                margin-bottom: 10px;
            }

            h2:after{
                content: '';
                position: absolute;
                left: 50%;
                bottom: 0;
                transform: translateX(-50%);
                height: 4px;
                width: 50px;
                border-radius: 2px;
                background-color: #BCEA73;
            }

            .field{
                min-width: 380px;
                width: 100%;
                height: 55px;
                background-color: #f0f0f0;
                margin: 10px 0;
                border-radius: 30px;
                display: grid;
                grid-template-columns: 15% 85%;
                padding: 0.5rem 1rem;

                .icon{
                    text-align: center;
                    line-height: 45px;
                    color: #acacac;
                    font-size: 1.1rem;
                }

                input{
                    background: none;
                    outline: none;
                    border: none;
                    line-height: 1.6;
                    font-size: 1.1rem;
                    color: black;
                }
            }
        }
    }
`

export default Signup
