import React from 'react';
import styled from 'styled-components';

function CommentSection() {
  return (
    <ContainerStyled>
      <div className='left-content'>
        Hi
      </div>
      <div className='right-content'>
        Hey
        <div className='textfield'>
          <textarea placeholder='Write review...' />
        </div>
        <button className='btn'>POST</button>
      </div>
    </ContainerStyled>
  )
}

const ContainerStyled = styled.div`
  display: grid;
  /* grid-template-columns: repeat(2, 1fr); */
  grid-template-columns: 2fr 40%;
  justify-content: center;
  align-items: center;
  padding: 1rem;

  .left-content{
    background: palevioletred;
  }

  .right-content{
    padding: .5rem;
    box-shadow: 10px 10px 20px #ced8f5, -10px -10px 20px #f6f9ff;

    .textfield{
      height: 200px;
      width: 100%;
      border-radius: 15px;
      margin: 1rem 0;

      textarea{
        height: 200px;
        width: 100%;
        outline: none;
        border: none;
        border-radius: 10px;
        line-height: 1.4;
        padding: .5rem;
        background: #F0F4FF;
        box-shadow: inset 3px 3px 10px 2px #ced8f5;
        font-family: 'Open Sans', sans-serif;
      }
    }

    .btn{
      width: 150px;
      height: 40px;
      padding: 0.5rem 1rem;
      font-size: 1.1rem;
      background-color: #BCEA73;
      cursor: pointer;
      outline: none;
      border: none;
      border-radius: 30px;
      color: white;
      transition: .3s;
    }

    .btn:hover{
        background-color: #B1DC6D;
    }
  }
`

export default CommentSection
